  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  @Input() inputData:any
  @Output()  outputData=new EventEmitter<string>();


  dataFromParent(){
    this.outputData.emit('parent to child class')
  }

}
